import React from 'react';//import react is provided the react types package(if you go into the node modules package we see the at types folder it should contain a react folder)
import TodoList from './components/TodoList';
import NewToDo from './components/NewToDo';

const App: React.FC = () => {
  const todo = [{ id: 't1', text: 'Finish the course' }];//we have an array which has an object t1 and a text

  //the function below will be called from the newtodo component
  const todoAddHandler= (text:string) =>{
    console.log(text);
  };

  return <div className="App">
    {/*A component that add todos,the below code we are passing props using item*/ }
    <NewToDo onAddTodo={todoAddHandler}/>
    <TodoList items={todo}/>
  </div>
};

export default App;
